
#Day 6.  Copy input string instead of writing to file

input_string = ''

char_length = 14
char_index  = char_length - 1

length = len(input_string)

mylist = []

count = 0

# set up list of current 14 characters...
while True:
    count +=1
    mylist.append(' ')
    if count > char_index:
        break

count = 0

#read input string by character ...
for i in input_string:

    # if we have full list we can proccess it.
    # move all chars one to left, replace last one with current char
    if count > char_index:
        for i in range(0, char_index):
            mylist[i] = mylist[i+1]  

        #replace last element    
        mylist[char_index] = input_string[count]

        #test for all chars being unique

        #buld a string from the current list
        string = ''
        for i in range(0, char_length):
            string += str(mylist[i])

        #create an array with the count for each letter (number of times each letter is in string, from position 1 to string_length)
        char_count = 0
        count_by_char = []
        for i in range(0, char_length):
            char_count = string.count(string[i])
            count_by_char.append(char_count)

        #if sum of list = length of unique chars we need then we found it (if greater then at least one char appears more than once)...
        total_sum = sum(count_by_char)

        if total_sum == char_length:
            print(count+1, sum(count_by_char), string)
            quit()
        
    #we don't have a full list yet.  Add here until we have a list of the first char_length of characters ...
    else:  

        mylist[count] = input_string[count]

    #next character
    count += 1





